import React, { useState, useEffect } from "react";
import $3Dmol from '3dmol/build/3Dmol';
import styled from 'styled-components';
import ClipLoader from "react-spinners/ClipLoader";
import ClimbingBoxLoader from 'react-spinners/ClimbingBoxLoader';

export default function AFoutput() {
  // CSS styles using styled-components
  const Wrap = styled.div`
    display: flex;
    flex-direction: row;
    background: #f7f7f7;
    max-width: 80%;
    margin: 0 auto;
  `;

  const Span = ({ space = 20 }) => {
    return <span style={{ paddingRight: space }}></span>;
  };

  const [loading, setLoading] = useState(false);
  const [ptid, setPtid] = useState("");

  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://3Dmol.org/build/3Dmol-min.js";
    script.async = true;
    document.body.appendChild(script);

    // Check local storage for proteinId
    const getDataLocalStorage = (name) => {
      const localData = localStorage.getItem(name);
      return localData;
    };

    if (localStorage.getItem("proteinId")) {
      const proteinId = getDataLocalStorage("proteinId");
      setPtid(proteinId);
      setLoading(false);
      console.log(proteinId);
    } else {
      setLoading(true);
    }
  }, []);

  useEffect(() => {
    if (ptid) {
      const element = document.getElementById("id");
      const config = { backgroundColor: 'orange' };
      const viewer = $3Dmol.createViewer(element, config);
      const getId = "pdb:" + ptid;

      $3Dmol.download(getId, viewer, {}, function() {
        viewer.setStyle({'model': -1}, {"cartoon": {'color': 'spectrum'}})
        viewer.zoomTo();
        viewer.render();
        viewer.zoom(0.8, 2000);
      });
    }
  }, [ptid]);

  return (
    <div className="page">
      {loading ? (
        <div className='container'>
          <ClimbingBoxLoader
            size={50}
            color="#123abc"
            loading={loading}
          />
        </div>
      ) : (
        <div className="page">
          <div id="id" className="mol-container"></div>
          <h1 className="korean-protein-expaination"></h1>
          <button>Download File</button>
        </div>
      )}
    </div>
  );
}
