import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import Prediction from "./prediction";
import HowAlpha from "./HowAlpha";
import HowRoseta from "./HowRoseta";

const Refer = () => {

  const menuList = ["prediction", "AlphaFold", "RosetaFold"];
  const [hide, setHide] = useState({
    prediction : false,
    AlphaFold : false,
    RosetaFold : false,
  });
  const mouseEvent = (menuName, bool) => {
    const change = { ...hide };
    change[menuName] = bool;
    setHide(change);
  };
  return (
    <nav className="nav">
      <ul className="navContainer">
        {menuList.map((v, idx) => (
          <li 
          className={hide[v] ? "active" : "none"}
          onMouseEnter={() => mouseEvent(v, true)}
          onMouseLeave={() => mouseEvent(v, false)}
          >
            <p>
              {`메뉴${idx + 1}`}
            </p>
          </li>
        ))}
      </ul>
      <div className="detailMenu">
        {menuList.map((v, idx) => (
          <ul
          onMouseEnter={() => mouseEvent(v, true)}
          onMouseLeave={() => mouseEvent(v, false)}
          >

          </ul>
        ))}
      </div>
    </nav>
  );
};
export default Refer;
