import React, { useState, useEffect } from "react";
import * as $3Dmol from '3dmol/build/3Dmol.js';
import $ from 'jquery';
import styled from 'styled-components';
import ClipLoader from "react-spinners/ClipLoader";
import ClimbingBoxLoader from 'react-spinners/ClimbingBoxLoader';

export default function AFoutput() {
  // CSS styling
  const Wrap = styled.div`
    display: flex;
    flex-direction: row;
    background: #f7f7f7;
    max-width: 80%;
    margin: 0 auto;
  `;

  function Span({ space = 20 }) {
    return (
      <span style={{ paddingRight: space }}></span>
    );
  }

  const [loading, setLoading] = useState(false);
  const [Protein_input, setProtein_input] = useState("");
  var ptid = "";

  // Load 3Dmol script
  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://3Dmol.org/build/3Dmol-min.js";
    script.async = true;
    document.body.appendChild(script);
  }, []);

  // Loading screen implementation
  useEffect(() => {
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 5000);
  }, []);

  const getDataLocalStorage = (name) => {
    var localData = localStorage.getItem(name);
    return localData;
  };

  if (localStorage.getItem("proteinId")) {
    ptid = getDataLocalStorage("proteinId");
    console.log(ptid);
  }

  // Visualization
  useEffect(() => {
    $(function () {
      let element = $('#id');
      let config = { backgroundColor: 'orange' };
      let viewer = $3Dmol.createViewer(element, config);
      let getId = "pdb:" + ptid;

      $3Dmol.download(getId, viewer, {}, function () {
        viewer.setStyle({ 'model': -1 }, { "cartoon": { 'color': 'spectrum' } })
        viewer.zoomTo();
        viewer.render();
        viewer.zoom(0.8, 2000);
      });
    });
  }, [ptid]);

  return (
    <div className="page">
      {loading ? (
        <div className="container">
          <ClimbingBoxLoader
            size={50}
            color={"#123abc"}
            loading={loading}
          />
        </div>
      ) : (
        <div className="page">
          <div id="id" className="mol-container"></div>
          <h1 className="korean-protein-expaination"></h1>
          <button>Download File</button>
        </div>
      )}
    </div>
  );
}
